            color: $txt-color;
            line-height: 40px;
            border: none;
            padding-left: 5px;
            border-radius:8px 0px 0px 8px;
            @include mq(mobile, max) {
                &{
                    display: none;
                }
        
            }

            &:focus {
                background-color: $bg-search;
                color: $txt-color;
                outline: none;
            }
            &::placeholder{
                color:$txt-color;
            }

        }

        &-icon {
            height: 50px;
            float: right;
            background-color: $bg-search;
            color: $txt-color;
            border-radius: 0px 8px 8px 0px;
            @include mq(mobile, max) {
                &{
                    background-color: $bg-color;
                }
        
            }
        }
    }

}

sources.assets\\scss\\mixing\\responsive.scss:
$breakpoints: (
    "mobile":       420px,
    "tablet":       1240px,
    "desktop":      1920px
);

@mixin mq($width, $type: min) {
    @if map_has_key($breakpoints, $width) {
        $width: map_get($breakpoints, $width);
        @if $type == max {
            $width: $width - 1px;
        }
        @media only screen and (#{$type}-width: $width) {
            @content;
        }
    }
}

