.header {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    background-color: $bg-color;
    height: 90px;                                                                                                                       

    &__logo {
        font-weight: 1000;
        margin-left: 20px;
    }

    &__text {
        font-size: 48px;
        line-height: 58px;
        color: $txt-color;
        text-decoration: none;
    }

    &__search {
        margin-right: 20px;

        &-area {
            width: 425px;
            height: 50px;
            font-size: 20px;
            background-color: $bg-search; 
            color: $txt-color;
            line-height: 40px;
            border: none;
            padding-left: 5px;
            border-radius:8px 0px 0px 8px;
            @include mq(mobile, max) {
                &{
                    display: none;
                }
        
            }

            &:focus {
                background-color: $bg-search;
                color: $txt-color;
                outline: none;
            }
            &::placeholder{
                color:$txt-color;
            }

        }

        &-icon {
            display: block;
           height: 50px;
           width: 50px;
           font-size: 36px;
           text-align: center;
           padding-top: 5px;
            float: right;
            background-color: $bg-search;
            color: $txt-color;
            border-radius: 0px 8px 8px 0px;
            @include mq(mobile, max) {
                &{
                    background-color: $bg-color;
                }
        
            }
        }
    }

}

sources.assets\\scss\\mixing\\responsive.scss:
$breakpoints: (
    "mobile":       650px,
    "tablet":       1024px,
    "desktop":      1920px
);

@mixin mq($width, $type: min) {
    @if map_has_key($breakpoints, $width) {
        $width: map_get($breakpoints, $width);
        @if $type == max {
            $width: $width - 1px;
        }
        @media only screen and (#{$type}-width: $width) {
            @content;
        }
    }
}

sources.assets\\scss\\poster.scss:
.poster {
    background-image: url(https://www.dekhnews.com/entertainment/wp-content/uploads/2021/05/Dni9Bdp2xTLZAS6LGSXbvM.jpg);
    background-size: cover;
    height: 500px;
    display: flex;
    flex-direction: column;
    padding-left: 40px;
    align-items: flex-start;
    justify-content: flex-end;
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    &__article {
        width: 100%;
        color: $txt-color;
        margin-bottom: 50px;
        
    }
    &__title {
        font-size: 60px;
        text-transform: capitalize;

        @include mq(tablet, max) {
            & {
                font-size: 48px;
            }
        }
        
    }
    &__content {
        margin-top: 20px;
        font-size: 24px;
        font-weight: bold;
        @include mq(mobile, max) {
            & {
                display: none;
            }
        }
        
    }
    &__rating {
        margin-bottom: 50px;
        display: flex;
        flex-direction: row;
    }
    &__star {
        color: $txt-color;
        font-size: 24px;

        @include mq(tablet, max) {
            & {
                font-size: 36px;
            }
        }
    }
    &__value {
        color: $txt-color;
        font-size: 24px;
        margin-left: 10px;
        font-weight: 700;
        @include mq(tablet, max) {
            & {
                font-size: 36px;
            }
        }
    }
}

